import { useState, useEffect } from "react";
import VideoCall from "./VideoCall";
import '../../styles/buyproduct.css';

const BuyProduct = () => {
  const [products, setProducts] = useState([]);
  const [selectedSeller, setSelectedSeller] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/marketplace")
      .then((response) => response.json())
      .then((data) => setProducts(data));
  }, []);

  const handleBuy = async (productId, price, sellerContact) => {
    try {
      const response = await fetch('http://localhost:5000/api/payment/payment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ productId, price, sellerContact }),
      });

      if (!response.ok) {
        throw new Error('Payment creation failed');
      }

      const data = await response.json();
      const razorpayOrderId = data.id;

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: price * 100,
        currency: 'INR',
        order_id: razorpayOrderId,
        handler: function (response) {
          alert('Payment successful!');
        },
        prefill: { name: 'Buyer Name', email: 'buyer@example.com', contact: '1234567890' },
        theme: { color: '#F37254' },
      };

      const rzp1 = new window.Razorpay(options);
      rzp1.open();
    } catch (error) {
      console.error('Payment error:', error);
    }
  };

  return (
    <div className="buy-product-container">
      <h2>Available Products</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product._id} className="product-card">
            <h3>{product.name}</h3>
            <p>Price: ₹{product.price}</p>
            <p>Seller: {product.seller}</p>
            <button onClick={() => handleBuy(product._id, product.price, product.sellerContact)}>Buy Now</button>
            <button onClick={() => setSelectedSeller(product.sellerContact)}>Video Call Seller</button>
          </div>
        ))}
      </div>
      {selectedSeller && <VideoCall sellerContact={selectedSeller} />}
    </div>
  );
};

export default BuyProduct;
